import React, { useState, useEffect } from "react";

const clientId = '367a1dd9d8fa4e7aac443fb3cd47e9fd'; // Replace with your Client ID
const clientSecret = '87d4c1ad132c440383be6992fb365c6c';// Replace with your Client Secret
const redirectUri = "http://localhost:3000/callback"; // Replace with your redirect URI
const scopes = "user-read-private user-read-email";

const SpotifySongSearch = () => {
  const [accessToken, setAccessToken] = useState("");

  const [query, setQuery] = useState("taylor swift");
  const [songs, setSongs] = useState([]);

  useEffect( ()=> {
    var url = 'https://accounts.spotify.com/api/token';
    var options = {
      method: 'POST',
      headers: {
        "Content-Type" : "application/x-www-form-urlencoded",
      }, 
      body: `grant_type=client_credentials&client_id=${clientId}&client_secret=${clientSecret}`
    }

    fetch(url, options).then(result => result.json()).then(data => setAccessToken(data.access_token));




  },[]);
  console.log(accessToken);
  async function SearchSong() {
    const url = 'https://api.spotify.com/v1/search?q=Taylor+Swift&type=artist';

    const authorizationSearch = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json', // Fixed incorrect 'applications/json' to 'application/json'
        "Authorization": `Bearer ${accessToken}` // Added a space after 'Bearer' and used template literals for clarity
      }
    };

    try {
      const res = await fetch(url, authorizationSearch);

      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const jsonRes = await res.json(); // Awaited the response JSON parsing
      console.log(jsonRes);
    } catch (error) {
      console.error('Error fetching song:', error);
    }
  }


};

export default SpotifySongSearch;
