import React, { useState, useEffect } from "react";

const clientId = '367a1dd9d8fa4e7aac443fb3cd47e9fd'; // Replace with your Client ID
const clientSecret = '87d4c1ad132c440383be6992fb365c6c';// Replace with your Client Secret
const redirectUri = "http://localhost:3000/callback"; // Replace with your redirect URI
const scopes = "user-read-private user-read-email";

const SpotifySongSearch = () => {
  const [accessToken, setAccessToken] = useState("");

  const [query, setQuery] = useState("");
  const [songs, setSongs] = useState([]);

  useEffect(async ()=> {
    const url = 'https://accounts.spotify.com/api/token';
    const options = {
      method: 'POST',
      header: {
        "Content-Type" : "application/x-www-form-urlencoded",
      }, 
      body: 'grant_type=client-credentials&client_id=' + clientId + '&client_secret=' + clientSecret, 
    }
    try{
      const res =  await fetch(url, options);
      const jsonRes =  res.json();
      setAccessToken(jsonRes);
    } catch(error) {
      console.log(error);
    }
  },[])





  return (
    <div style={{ fontFamily: "Arial, sans-serif", margin: "20px" }}>
      <h1>Spotify Song Search</h1>
      {!accessToken ? (
        <button onClick={handleLogin}>Log in to Spotify</button>
      ) : (
        <div>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search for a song..."
            style={{ marginRight: "10px" }}
          />
          <button onClick={handleSearch}>Search</button>
          <ul style={{ marginTop: "20px" }}>
            {songs.map((song) => (
              <li key={song.id}>
                {song.name} by {song.artists.map((artist) => artist.name).join(", ")}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SpotifySongSearch;
